<?php/** * 'bws_group_button' Shortcode *  * @package Buttons With Style Pro * @since 1.0.0 */// Exit if accessed directlyif ( !defined( 'ABSPATH' ) ) exit;function bwsp_group_button( $atts, $content = null ) {	// Shortcode Parameter	extract(shortcode_atts(array(		'id' => 0	), $atts));	// If id is not there then return	if( empty($id) ) {		return $content;	}	// Taking some variables	$prefix 	= BWSPOS_PRO_META_PREFIX; // Metabox prefix	$count 		= 1;	// Getting post data	$post_data = get_post($id);	if(isset($post_data->post_type) && $post_data->post_type == BWSPOS_PRO_POST_TYPE && $post_data->post_status == 'publish') {		// Getting button type		$choice_button_type = get_post_meta( $id, $prefix.'choice_button_type', true );		// If button is group button		if($choice_button_type == 'button_group') {			$post_meta 			= bwsp_get_button_post_meta($id);			$target 			= $post_meta['button_link_target'];			$button_main_class 	= "bws-button bws-{$post_meta['button_class']}  bws-{$post_meta['button_style']} bws-{$post_meta['button_size']}";			ob_start();	?>		<ul class="bws-button-group <?php echo 'bws-'.$post_meta['button_style_type']; ?>">			<?php				if( !empty($post_meta['grp_btn_data']) ) {					$btn_width 			= (100 / count($post_meta['grp_btn_data']));					$btn_width_style	= ($post_meta['button_size'] == 'expand') ? "width:{$btn_width}%;" : '';					foreach ($post_meta['grp_btn_data'] as $grp_key => $grp_val) {						$btn_name = isset($grp_val['name']) 	? $grp_val['name'] 		: '';						$icon_cls = isset($grp_val['icon_cls']) ? $grp_val['icon_cls'] 	: '';						$btn_link = !empty($grp_val['link']) 	? $grp_val['link'] 		: 'javascript:void(0);';					?>						<li style="<?php echo $btn_width_style; ?>">							<a href="<?php echo $btn_link; ?>" class="<?php echo $button_main_class; ?>"  target="<?php echo $target; ?>">								<?php if ($icon_cls) { ?>								<i class="bws-icon bws-icon-size-<?php echo $post_meta['button_icon_size']; ?> fi-<?php echo $icon_cls; ?>"></i>								<?php }								if( $btn_name ) { ?>								<span class="bws-btn-name"><?php echo $btn_name; ?></span>								<?php } ?>							</a>						</li>			<?php						$count++;					} // End foreach				} // End of if			?>		</ul>			<?php		$content .= ob_get_clean();		}	} // End of main if	return $content;}// 'bws_group_button' shortcodeadd_shortcode('bws_group_button', 'bwsp_group_button');