<?php/** * 'bws_button' Shortcode *  * @package Buttons With Style Pro * @since 1.0.0 */// Exit if accessed directlyif ( !defined( 'ABSPATH' ) ) exit;function bwsp_simple_button( $atts, $content = null ) {		// Shortcode Parameter	extract(shortcode_atts(array(		'id'     		=> 0			), $atts));	// If id is not there then return	if( empty($id) ) {		return $content;	}	// Taking some variables	$prefix = BWSPOS_PRO_META_PREFIX; // Metabox prefix	// Getting post data	$post_data = get_post($id);	/*echo "<pre>";	var_dump($post_data);	echo "</pre>";*/	// If it is button post type and post is publish	if(isset($post_data->post_type) && $post_data->post_type == BWSPOS_PRO_POST_TYPE && $post_data->post_status == 'publish') {				// Getting button type		$choice_button_type = get_post_meta( $id, $prefix.'choice_button_type', true );				// If it is a simple button		if($choice_button_type == 'button') {			$post_meta 			= bwsp_get_button_post_meta($id);			$btn_name 			= isset($post_meta['simple_button_text']) 	? $post_meta['simple_button_text'] 	: '';			$icon_cls 			= isset($post_meta['button_icon_class']) 	? $post_meta['button_icon_class'] 	: '';			$link 				= !empty($post_meta['simple_button_link']) ? $post_meta['simple_button_link'] : 'javascript:void(0);';			$target 			= $post_meta['button_link_target'];			$button_main_class 	= "bws-button bws-{$post_meta['button_class']} bws-{$post_meta['button_style_type']} bws-{$post_meta['button_style']} bws-{$post_meta['button_size']}";			if( empty($btn_name) && empty($icon_cls) ) {				return $content;			}			ob_start();	?>	<?php 		/*echo "<h2>Recent Posts</h2><ul>";    $args = array( 'numberposts' => '5' );    $recent_posts = wp_get_recent_posts( $args );    foreach( $recent_posts as $recent ){    	echo  $recent['ID'];    	echo get_permalink( $recent['ID']);    	echo '<Br>';    	printf(get_permalink( $recent['ID'],true));       printf( '<li><a href="%s">%s</a></li>',             esc_url( get_permalink( $recent['ID'] ) ),             $recent['post_title']         );    }	echo "</ul>";*/?>			<a href="<?php echo $link; ?>" class="<?php echo $button_main_class ;?>" target="<?php echo $target; ?>">				<?php if ($icon_cls) { ?>				<i class="bws-icon bws-icon-size-<?php echo $post_meta['button_icon_size']; ?> fi-<?php echo $icon_cls; ?>"></i>				<?php }				if( $btn_name ) { ?>				<span class="bws-btn-name"><?php echo $btn_name; ?></span>				<?php } ?>			</a>	<?php			$content .= ob_get_clean();		}	}	return $content;}// 'bws_button' shortcodeadd_shortcode('bws_button', 'bwsp_simple_button');