<div class="wpsisac-slick-image-slide">	<div class="wpsisac-slide-wrap">			<div class="wpsisac-img-wrap" <?php echo $slider_height_css; ?>><?php			if( $slider_img ) { ?>				<img class="wpsisac-slider-img" src="<?php echo $slider_img; ?>" alt="<?php _e('Slider Carousel Image', 'wp-slick-slider-and-image-carousel'); ?>" />			<?php } ?>		</div>				<?php echo ( !empty($read_more_url) ? '<a href="'.esc_url($read_more_url).'" target="'.$link_target.'">' : ''); ?>			<div class="wpsisac-slider-content">								<h4 class="wpsisac-slide-title">					<?php the_title(); ?>				</h4>				<div class="wpsisac-show-hide-content"><?php					if($showContent == "true" ) { ?>						<div class="wpsisac-slider-short-content"><?php							the_content(); ?>						</div><?php					}					if( !empty($read_more_url) ) { ?>						<div class="wpsisac-readmore">							<?php echo $read_more_text; ?>												</div><?php					} ?>				</div>			</div>		<?php echo (!empty($read_more_url) ? '</a>' : ''); ?>	</div></div>